
public class CheckStringEmpty {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String x="Keshav";
		int len=x.length();
		
		if(len==0) {
			
			System.out.println("String is empty");
		}
		else {
			System.out.println("String is not empty");
		}

	}

}
